import React from "react";
import { Form, Input, Button, Card, Icon, Avatar } from "antd";
const dummy = {
  isLoggedIn: false,
  imagePaths: [],
  mainPosts: [
    {
      User: {
        id: 1,
        nickname: "넛지"
      },
      content: "뭐라는지 하나도 모르겠네",
      img: "https://mimgnews.pstatic.net/image/382/2019/05/20/0000732741_002_20190520094010708.jpg?type=w540"
    }
  ]
};

const Home = () => {
  return (
    <div>
      {dummy.isLoggedIn && (
        <Form encType="multipart/from-data">
          <Input.TextArea maxLength={140} placeholder="이게 최선이야?" />
          <div>
            <Input type="file" multiple hidden />
            <Button>이미지 업로드</Button>
            <Button type="primary" style={{ float: "right" }} htmlType="submit">
              뀍뀍
            </Button>
          </div>
          <div>
            {dummy.imagePaths.map((v, i) => {
              return (
                <div key={v} style={{ display: "inline-block" }}>
                  <img src={"https://localhost:3065/" + v} style={{ width: "200px" }} alt={v} />
                  <div>
                    <Button>제거</Button>
                  </div>
                </div>
              );
            })}
          </div>
        </Form>
      )}
      {dummy.mainPosts.map(c => {
        return (
          <Card
            key={+c.createdAt}
            cover={c.img && <img alt="example" src={c.img} />}
            actions={[
              <Icon type="retweet" key="retweet" />,
              <Icon type="heart" key="heart" />,
              <Icon type="message" key="message" />,
              <Icon type="ellipsis" key="ellipsis" />
            ]}
            extra={<Button>팔로우</Button>}
          >
            <Card.Meta avatar={<Avatar>{c.User.nickname[0]}</Avatar>} title={c.User.nickname} description={c.content} />
          </Card>
        );
      })}
    </div>
  );
};

export default Home;

// <Form encType="multipart/from-data" /> 이미지업로드를 위함
